mapper.insert:
INSERT INTO CENTER(
     center_code
   , center_name
   , center_tel
   , center_address
   , center_guide
   , center_opening_date
   , center_closing_date)
VALUES (
     #{centerCode}
   , #{centerName}
   , #{centerTel}
   , #{centerAddress}
   , #{centerGuide}
   , #{centerOpeningDate,jdbcType=DATE}
   , #{centerClosingDate,jdbcType=DATE})

INSERT INTO fileinfo (
	file_no
	, center_code
	, file_saved_name
	, file_original_name
	, file_type
	, file_detail
	, file_path
	, file_post_date 
	, file_upload_user_code
	)
VALUES (
	(SELECT NVL(MAX(file_no)+1, 1) FROM fileinfo)
	, #{centerCode}
	, #{fileSavedName}
	, #{originalName}
	, #{fileType}
	, #{fileDetail}
	, #{filePath}
	, SYSDATE 
	, #{uploadUserCode}
	)

mapper.update:
UPDATE fileinfo 
SET file_original_name = #{originalName}
	, file_detail = #{fileDetail}
	, file_saved_name = #{fileSavedName}
	, file_modify_date = SYSDATE
WHERE file_no = #{fileNo}
