mapper.insert:
INSERT INTO CENTER(
	  center_code
	, center_name
	, center_tel
	, center_address
	, center_guide
	, center_opening_date
	, center_closing_date)
VALUES (
	  #{centerCode}
	, #{centerName}
	, #{centerTel}
	, #{centerAddress}
	, #{centerGuide}
	, #{centerOpeningDate,jdbcType=DATE}
	, #{centerClosingDate,jdbcType=DATE})

INSERT INTO fileinfo (
	file_no
	, center_code
	, file_saved_name
	, file_original_name
	, file_type
	, file_detail
	, file_path
	, file_post_date )
VALUES (
	(SELECT NVL(MAX(file_no)+1, 1) FROM fileinfo)
	, #{centerCode}
	, #{fileSavedName}
	, #{originalName}
	, #{fileType}
	, #{fileDetail}
	, #{filePath}
	, SYSDATE )
