mapper.select:
  
	SELECT user_code as userCode, user_name as userName, user_password as userPassword, user_email as userEmail
    FROM userinfo
    WHERE user_code=#{userCode}

mapper.update:
 
	UPDATE userinfo
    SET user_password =#{userPassword}
    WHERE user_code=#{userCode}

mapper.select:
SELECT user_password as userPassword
FROM userinfo
WHERE user_code= #{userCode}
