mapper.select:
SELECT I.center_code as "centerCode", I.user_code as "userCode", I.check_detail_code as "checkDetailCode", I.check_year as "checkYear", I.check_season as "checkSeason", D.check_group_code as "checkGroupCode", G.check_group_content as "checkGroupContent", D.check_detail_content as "checkDetailContent", I.check_score as "checkScore" FROM check_detail D LEFT OUTER JOIN Inspection I ON D.check_group_code = I.check_group_code AND D.check_detail_code = I.check_detail_code LEFT OUTER JOIN check_group G ON I.check_group_code = G.check_group_code WHERE I.center_code = #{centerCode}
AND I.check_year = #{checkYear}
AND I.check_season = #{checkSeason}
ORDER BY I.check_year DESC, I.check_season DESC NULLS LAST

mapper.update:
UPDATE
    inspection 
SET
    check_score = #{checkScore}
WHERE 
    center_code =#{centerCode}
AND
    user_code = #{userCode}
AND
    check_group_code = #{checkGroupCode}
AND 
    check_detail_code = #{checkDetailCode}
AND check_year = #{checkYear}
AND check_season = #{checkSeason};

UPDATE check_detail 
SET check_detail_content = NVL(#{detailContent, jdbcType=VARCHAR}, check_detail_content)
	, check_detail_occupied = #{detailOccupied}
WHERE check_group_code = #{groupCode} AND check_detail_code = #{detailCode}

UPDATE check_group
SET check_group_content = #{groupContent}
	, check_group_occupied = #{groupOccupied}
WHERE check_group_code = #{groupCode}
